 <script>
        $(document).ready(function(){
            $('a[id^="category_"]').on('click', function(e){
                e.preventDefault(); // Prevent default action of the anchor tag

                var categoryId = $(this).data('category-id'); // Get the category ID from data attribute
                var url = 'fetch_products.php?category_id=' + categoryId; // URL to fetch products
                alert(categoryId);
                // AJAX call to fetch products
                $.ajax({
                    url: url,
                    type: 'GET',
                    dataType: 'json',
                    success: function(response){
                        if(response.success){
                            // Clear previous products
                            $('#productContainer').empty();

                            // Append new products
                            $.each(response.data, function(index, product){
                                $('#productContainer').append(
                                    '<div class="masonry-item cat-' + categoryId + ' col-lg-4 col-md-6 col-sm-6 m-b30">' +
                                        '<div class="project-img-effect-1">' +
                                            '<img src="' + product.thumbnail + '" alt="' + product.product_name + '" />' +
                                            '<div class="wt-info">' +
                                                '<h3 class="wt-tilte m-b10 m-t0">' + product.product_name + '</h3>' +
                                                '<p></p>' +
                                                '<a href="#" class="site-button-link contact-slide-show"> Download Brochure</a>' +
                                            '</div>' +
                                            '<a href="' + product.thumbnail + '" class="mfp-link"><i class="fa fa-search-plus"></i></a>' +
                                        '</div>' +
                                    '</div>'
                                );
                            });
                        } else {
                            console.log(response.message); // Log error message
                        }
                    },
                    error: function(xhr, status, error){
                        console.error(error); // Log AJAX error
                    }
                });
            });
        });
    </script>




